import React, { Component } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import Input from '../../components/uielements/input';
import Checkbox from '../../components/uielements/checkbox';
import Button from '../../components/uielements/button';
// import authAction from '../../redux/auth/actions';
import IntlMessages from '../../components/utility/intlMessages';
import UserStyleWrapper from './user.style';
import {
  Form,notification ,Table,  Popconfirm ,Modal
} from "antd";
import { siteConfig } from '../../settings';
import { addUser } from '../../actions/actions';


class User extends Component {
  constructor(props){
  super(props)
  this.state = {
    visible :false,
    dataSource: [
      {
        key: '0',
        name: 'Edward King 0',
        age: '32',
        address: 'London, Park Lane no. 0',
      },
      {
        key: '1',
        name: 'Edward King 1',
        age: '32',
        address: 'London, Park Lane no. 1',
      },
    ],
    count: 2,
    loading :false
  };
  this.columns = [
    {
      title: 'name',
      dataIndex: 'name',
      width: '30%',
      editable: true,
    },
    {
      title: 'age',
      dataIndex: 'age',
    },
    {
      title: 'address',
      dataIndex: 'address',
    },
    {
      title: 'operation',
      dataIndex: 'operation',
      render: (text, record) =>
        this.state.dataSource.length >= 1 ? (
          <Popconfirm title="Sure to delete?" onConfirm={() => this.handleDelete(record.key)}>
            <a>Delete</a>
          </Popconfirm>
        ) : null,
    },
  ];
  }
  handleDelete = key => {
    const dataSource = [...this.state.dataSource];
    this.setState({ dataSource: dataSource.filter(item => item.key !== key) });
  };

  handleAdd = () => {
    const { count, dataSource } = this.state;
    const newData = {
      key: count,
      name: `Edward King ${count}`,
      age: 32,
      address: `London, Park Lane no. ${count}`,
    };
    this.setState({
      dataSource: [...dataSource, newData],
      count: count + 1,
      visible: true,
    });
  };

  handleSave = row => {
    const newData = [...this.state.dataSource];
    const index = newData.findIndex(item => row.key === item.key);
    const item = newData[index];
    newData.splice(index, 1, {
      ...item,
      ...row,
    });
    this.setState({ dataSource: newData });
  };
  componentDidUpdate(prevProps, prevState) {
    let self = this;
    if (prevProps.add_user !== self.props.add_user) {
        self.setState({
          loading:false
        });
        if(self.props.add_user.length !=0){      
          notification['success']({
            message: 'Added sucessfuly !!!',
            description:'',
          });
        }else{
          notification['error']({
            message: 'Something went wrong !!!',
            description:'',
          });
        }
    }
  }
  handleAddUser = (e) => {
    var self = this;
    this.setState({
      loading :true ,
      visible: false,
      
    });
    const { dispatch } = this.props;
    e.preventDefault();


    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log(values)
        dispatch(addUser(values));
      }
    });
  };

render() {
    const from = { pathname: '/dashboard' };
    const { getFieldDecorator } = this.props.form;
    const { redirectToReferrer  ,loading,dataSource } = this.state;
    // const components = {
    //   body: {
    //     row: EditableFormRow,
    //     cell: EditableCell,
    //   },
    // };
    const columns = this.columns.map(col => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onCell: record => ({
          record,
          editable: col.editable,
          dataIndex: col.dataIndex,
          title: col.title,
          handleSave: this.handleSave,
        }),
      };
    });


    return (
      
      <div style={{width:'100%',display:'block',height:'100%'}}>
        <Modal
          title="Add User Detail"
          visible={this.state.visible}
          onOk={this.handleAdd}
          onCancel={this.handleCancel}
        >
    {/* <div style={{width:'100%',overflow:'hidden',height:window.innerHeight+'px',float:'left',backgroundImage:'url("")',backgroundSize: 'cover'}}> */}
        <UserStyleWrapper className="isoSignInPage">
        <div className="isoLoginContentWrapper">
          <div className="isoLoginContent">
            <div className="isoSignInForm">
            <Form onSubmit={this.handleAddUser} className="login-form">
        <Form.Item>
          {getFieldDecorator('title', {
            rules: [{ required: true, message: 'Please input your Title!' }],
          })(
              <div className="isoInputWrapper">
                <Input size="large" placeholder="Title" />
              </div>
          )}
          </Form.Item>
         
          <Form.Item>
          {getFieldDecorator('body', {
            rules: [{ required: true, message: 'Please input your body!' }],
          })(
              <div className="isoInputWrapper">
                <Input size="large" type="text" placeholder="Body"  />
              </div>
          )}
          </Form.Item>
          <Form.Item>
          {getFieldDecorator('userId', {
            rules: [{ required: true, message: 'Please input your id!' }],
          })(
              <div className="isoInputWrapper">
                <Input size="large" type="number" placeholder="Number"  />
              </div>
          )}
          </Form.Item>
              {/* <div className="isoInputWrapper isoLeftRightComponent">
                <Button type="primary"  htmlType="submit" loading={loading} style={{backgroundColor : '#3f51b5',width:'100%'}}>
                  <IntlMessages id="page.signInButton" />
                </Button>
              </div> */}
              </Form>
              
            </div>
          </div>
        </div>
      </UserStyleWrapper> 
      {/* </div> */}
            </Modal>
        <div>
        <Button onClick={this.handleAdd} type="primary" style={{ marginBottom: 16 }}>
          Add a row
        </Button>
        <Table
          // components={components}
          rowClassName={() => 'editable-row'}
          bordered
          dataSource={dataSource}
          columns={columns}
        />
      </div>    
      </div>
    );
  }
}
User = Form.create()(User);
function mapStateToProps(state) {
  
  const { Common } = state;
  const {
    add_user  
  } = Common;

  return {
    add_user
  };
}

const AppContainer = connect(mapStateToProps)(User);
export default AppContainer;
